parseFile(files) {
    meta := getFileMeta
    rootNote = @parseNoteProps
    maxLvl := meta

    // 1st level notes
    lvl = 1
    prevNodes = meta[lvl]
    prevNodes.map  {
        note = @parseNoteProps it
        if !(note in $cache) { updateCache note }
        return note
    }
    prevNodes.map {
        addChild(rootNote, it)
    }
    lvl++

    // remaining notse
    while lvl < maxLvl {
        notes = meta[lvl]];
        notes.map {
            note = @parseNoteProps it
        }
    }
}

